mapper.select:
		SELECT RN,SEQ,ID,TITLE,CONTENT,REGDATE,REFER,STEP,DEPTH,READCOUNT,DELFLAG
     	 FROM(
      	SELECT ROW_NUMBER() OVER (ORDER BY REFER,STEP) AS RN,SEQ,ID,TITLE,CONTENT,REGDATE,REFER,STEP,DEPTH,READCOUNT,DELFLAG FROM M_BOARD      	 
     
	     	 )       	
      	WHERE CEIL(RN/10) = #{pnum}
